CREATE TABLE FORESTRY_TYPE ( ID NUMBER(19) NOT NULL, DESCRIPTION VARCHAR2(2000), NAME VARCHAR2(200) NOT NULL, PRIMARY KEY (ID) );

CREATE TABLE ATTACHMENT ( ID NUMBER(19) NOT NULL, FILE_NAME VARCHAR2(100), FILE_PATH VARCHAR2(1000), FORESTRYTYPE_ID NUMBER(19), PRIMARY KEY (ID) );
ALTER TABLE ATTACHMENT ADD CONSTRAINT FK8AF7592335EB87D FOREIGN KEY (FORESTRYTYPE_ID) REFERENCES FORESTRY_TYPE (ID);

CREATE TABLE AUTHORITY ( ID NUMBER(19) NOT NULL, BUTTONS VARCHAR2(50), CHECKED INTEGER, EXPANDED INTEGER DEFAULT 0  NOT NULL, ICON_CLS VARCHAR2(20), LEAF INTEGER DEFAULT 0  NOT NULL, MENU_CODE VARCHAR2(50) NOT NULL, MENU_CONFIG VARCHAR2(200), MENU_NAME VARCHAR2(50) NOT NULL, PARENT_ID NUMBER(19), SORT_ORDER NUMBER(10) NOT NULL, URL VARCHAR2(100), PRIMARY KEY (ID) );

CREATE TABLE ROLE_AUTHORITY ( ID NUMBER(19) NOT NULL, AUTHORITY_ID VARCHAR2(10) NOT NULL, ROLE NUMBER(5) NOT NULL, PRIMARY KEY (ID) );

CREATE TABLE SYS_USER ( ID NUMBER(19) NOT NULL, EMAIL VARCHAR2(30), LAST_LOGINTIME DATE, PASSWORD VARCHAR2(32) NOT NULL, REAL_NAME VARCHAR2(30), ROLE NUMBER(5) NOT NULL, TEL VARCHAR2(15), USER_NAME VARCHAR2(30) NOT NULL, PRIMARY KEY (ID), CONSTRAINT SYS_C0011402 UNIQUE (USER_NAME) );

CREATE TABLE SENSOR_DATA ( ID NUMBER(19) NOT NULL, RECORD_TIME DATE, SENSOR_ID NUMBER(10), SENSOR_TYPE NUMBER(5), SENSOR_VALUE FLOAT(126), PRIMARY KEY (ID) );

CREATE TABLE SENSOR_LASTDATA ( ID NUMBER(19) NOT NULL, SENSOR_ID NUMBER(10), SENSOR_LASTVALUE FLOAT(126), SENSOR_TYPE NUMBER(5), UPDATE_TIME DATE, PRIMARY KEY (ID) );

CREATE TABLE SENSOR ( ID NUMBER(19) NOT NULL, EPC_ID VARCHAR2(100), SENSOR_ID NUMBER(10), TYPE NUMBER(5), XCOORDINATE FLOAT(126), YCOORDINATE FLOAT(126), PRIMARY KEY (ID) );

CREATE TABLE FORESTRY ( ID NUMBER(19) NOT NULL, ENTRY_TIME DATE, EPC_ID VARCHAR2(100) NOT NULL, NAME VARCHAR2(200) NOT NULL, PLANT_TIME DATE, TYPE_ID NUMBER(19), PRIMARY KEY (ID) );
ALTER TABLE FORESTRY ADD CONSTRAINT FK1BD95724536DA021 FOREIGN KEY (TYPE_ID) REFERENCES FORESTRY_TYPE (ID);

CREATE TABLE CONFIG ( ID NUMBER(19) NOT NULL, CONFIG_TYPE NUMBER(5) NOT NULL, CONFIG_VALUE NUMBER(10), PRIMARY KEY (ID) );

CREATE TABLE MONITOR_LOG ( ID NUMBER(19) NOT NULL, DISARM INTEGER DEFAULT 0  NOT NULL, LOG_TYPE NUMBER(5), MESSAGE VARCHAR2(256), OBJECT_ID NUMBER(10), VALUE FLOAT(126), PRIMARY KEY (ID) );

CREATE TABLE DEPARTMENT ( ID NUMBER(19) NOT NULL, NAME VARCHAR2(50), REMARK VARCHAR2(100), PRIMARY KEY (ID) );
